const regex = /^(https?:\/\/)(www\.)?[a-zA-Z0-9.-]+\.[a-zA-Z]{2,}(\/[^\/\s].*)?$/
const googleSearch = $input.item.json.organic_results
const braveSearch = $input.item.json.web.results
const googleUrls = googleSearch.map((search) => search.link)
const braveUrls = braveSearch.map((search) => search.url)
const urls = [...googleUrls, ...braveUrls]
const filterUrls = urls.filter((url) => regex.test(url))

return { filterUrls };

const filterUrl = $('extract_urls').first().json.filterUrls
const ourUrlsByCity = $('data_filter').all()
const newFilterUrlsResult = filterUrl.filter((webUrl) => {
  return ourUrlsByCity.some((allowUrl) => 
    webUrl.includes(allowUrl.json.url)
  )
})

return { allowUrls: newFilterUrlsResult }

// Loop over input items and add a new field called 'myNewField' to the JSON of each one
for (const item of $input.all()) {
  item.json.myNewField = 1;
}
const noNulls = $input.all().reduce((acc, el, idx) => {
  if (
    el.json.output.url &&
    el.json.output.precio &&
    el.json.output.nombre
  ) {
    const regex = /^(https?:\/\/)?([^\/\?\#]+)/i;
    const url =  $("create_item_url_mdData")
        .all()[idx]
        .json
        .result
        .url
    const match = url.match(regex);
    return [...acc, {
      url,
      precio: el.json.output.precio,
      nombre: el.json.output.nombre,
      domain: match?.[2] ?? undefined
    }]
  }

  return acc
}, [])

return { result: noNulls }

